var Ac4yPersistent=require("@ac4y/ac4y-common/object/Ac4yPersistent.js"); 

class Ac4yStreamAlgebra extends Ac4yPersistent {

    constructor(aObject) {

        super(aObject);
        if (aObject) this.rebuild(aObject, this);

    } // constructor


    createSelf() {
        return new Ac4yStreamAlgebra();

    } // createSelf

    setStream(aStream) {
        this.stream = aStream;
    }
    getStream() {
        return this.stream;
    }
    hasStream() {
        return this.stream != undefined;
    }


    rebuild(aObject, aTarget) {

        super.rebuild(aObject, aTarget);
        if (aObject) {

            if (aObject.stream != undefined) aTarget.setStream(aObject.stream);

        } // if aObject does not empty

        return aTarget;

    } // rebuild

} // Ac4yStreamAlgebra

module.exports=Ac4yStreamAlgebra
