var Ac4yStateSetAlgebra=require("@ac4y/ac4y-common/algebra/Ac4yStateSetAlgebra.js");
var Ac4yObjectHandler=require("@ac4y/ac4y-common/utility/Ac4yObjectHandler.js");

class Ac4yStateSet extends Ac4yStateSetAlgebra {

    forPersistence(){

        var object = this.createSelf(this);

        object.check();

        object = new Ac4yObjectHandler().deleteObjectTypeProperties(object);

        object.timestamp=new Date(object.timestamp);

        //console.log(object);

        return object;

    } // forPersistence

    getShortInformationObject(){

        return {
            "stĂˇtusz" : this.getState()
            ,"idĹ‘pont" : new Ac4yDateTimeHandler().getDateTimeExternalFormat(new Date(this.getTimestamp()))
        };

    } // getShortInformationObject

} // Ac4yStateSet

module.exports=Ac4yStateSet
